INSERT INTO `trangsuc` VALUES (2, 'Vòng Tay Đá Bánh Xe Pháp Luân - BXPL', '80,000', 'Trong phong thủy, bánh xe Pháp Luân còn được gọi là bánh xe chân lý, cuộc đời, vũ trụ. Nó là một trong những dấu hiệu may mắn, được cho là xuất hiện ở lòng bàn chân Đức Phật', '10', '10', NULL, '121.jpg,122.jpg,123.jpg,124.jpg,125.jpg,126.jpg', '<div><p style=\"text-align:center;\"><span style=\"font-size:18pt;color:rgb(255,0,0);\"><strong>VÒNG ĐÁ THẠCH ANH ĐEN MIX </strong></span></p><p style=\"text-align:center;\"><span style=\"font-size:18pt;color:rgb(255,0,0);\"><strong>BÁNH XE PHÁP LUÂN</strong></span></p><ul><li style=\"text-align:left;\"><span style=\"font-size:18pt;font-family:arial, helvetica, sans-serif;\"><span style=\"color:#ff0000;\"><b>Đá thạch anh đen 10ly</b></span></span><br></li><li style=\"text-align:left;\"><span style=\"color:rgb(255,0,0);font-size:18pt;font-family:arial, helvetica, sans-serif;\"><b>Bánh xe Pháp luân màu bạc Tây Tạng</b></span></li><li style=\"text-align:left;\"><span style=\"color:rgb(255,0,0);font-size:18pt;\"><b>Hàng đảm bảo như hình 100%</b></span></li><li style=\"text-align:left;\"><span style=\"color:rgb(255,0,0);font-size:18pt;\"><b>Có kèm thêm 2 viên đá và dây để tự xâu vòng (Tăng kích cỡ vừa size tay) = &gt; Nam nữ đều có thể đeo được.</b></span></li></ul><p><span style=\"color:#ff0000;\"><span style=\"font-size:18.6667px;\"><b><br></b></span></span></p><p style=\"text-align:left;\"><img src=\"//media3.scdn.vn/img3/2019/6_28/dfA3lI_simg_d0daf0_800x1200_max.jpg\" style=\"margin:0px auto;display:block;\" alt=\"dfA3lI_simg_d0daf0_800x1200_max.jpg\"><br><br><br></p><p style=\"padding-left:30px;text-align:left;\"><span style=\"font-size:18pt;color:rgb(33,37,41);font-family:arial, helvetica, sans-serif;\">Trong phong thủy, bánh xe Pháp Luân còn được gọi là bánh xe chân lý, cuộc đời, vũ trụ. Nó là một trong những dấu hiệu may mắn, được cho là xuất hiện ở lòng bàn chân Đức Phật. </span></p><p style=\"padding-left:30px;text-align:left;\"><span style=\"font-size:18pt;color:rgb(33,37,41);font-family:arial, helvetica, sans-serif;\">Bánh xe Pháp chuyển động không ngừng, giáo lý của Đức Phật phát triển không ngừng, hợp thời, hợp cơ, hợp lý, nhưng công năng vẫn là di chuyển, đưa chúng sanh từ tối đến sáng, từ khổ đến vui, từ thấp lên cao, từ vô minh đến Giác Ngộ, từ địa ngục tới Niết-Bàn.</span></p><p style=\"text-align:center;\"><span style=\"font-size:18pt;color:rgb(33,37,41);\">Pháp luân tượng trưng cho “pháp” hay lời dạy của Đức Phật giúp chúng sinh tìm đến con đường giác ngộ và giải thoát. Pháp luân là để hàng phục mọi tà kiến ngoại đạo. </span></p><p style=\"text-align:center;\"><span style=\"font-size:18pt;color:rgb(33,37,41);\">Chuyện phong thủy trước giờ vẫn là điều khó giải thích. Có thể bạn không tin, nhưng những thứ nhỏ bé đó lại đem lại sự kỳ diệu vô cùng.</span></p><p style=\"text-align:justify;\"><span style=\"font-size:24pt;\"><span style=\"font-size:18pt;color:rgb(33,37,41);\">Nếu bạn là người khá tự ti về bản thân thì <strong>ĐEO VÒNG ĐÁ BÁNH XE PHÁP LUÂN </strong>sẽ mang đến cho bạn rất nhiều bất ngờ, giúp những người nhát gan, tự ti, giúp con người có dũng khí và động lực vượt qua gian nan thử thách, tự tin vào chính mình.</span><strong><br></strong></span></p><p><br></p><p style=\"text-align:center;\"><span style=\"font-size:18pt;\"></span></p><p style=\"text-align:center;\"><span style=\"font-size:18pt;\"><img src=\"//media3.scdn.vn/img3/2019/6_28/JlFt3s_simg_d0daf0_800x1200_max.jpg\" style=\"margin:0 auto;display:block;\" alt=\"JlFt3s_simg_d0daf0_800x1200_max.jpg\"><br><br><br><br><br><br><img src=\"//media3.scdn.vn/img3/2019/6_28/VfVdMw_simg_d0daf0_800x1200_max.jpg\" style=\"margin:0 auto;display:block;\" alt=\"VfVdMw_simg_d0daf0_800x1200_max.jpg\"><br><br><br></span></p><p style=\"text-align:center;\"><span style=\"font-size:18pt;\"><img src=\"//media3.scdn.vn/img3/2019/6_28/oP7Xcq_simg_d0daf0_800x1200_max.jpg\" style=\"margin:0 auto;display:block;\" alt=\"oP7Xcq_simg_d0daf0_800x1200_max.jpg\"><br><br><br></span></p><p style=\"text-align:center;\"><span style=\"font-size:24pt;\"><strong></strong></span></p><p style=\"text-align:justify;\"><span style=\"font-size:18pt;color:rgb(0,0,0);\"></span></p><h5 style=\"text-align:justify;\"><span style=\"font-size:18pt;font-family:arial, helvetica, sans-serif;\"><strong><span style=\"color:rgb(17,17,17);\">Thông tin cơ bản về vòng đá thạch anh đen</span></strong></span></h5><h5 style=\"text-align:justify;\"><span style=\"font-size:18pt;color:rgb(17,17,17);font-family:arial, helvetica, sans-serif;\">Vòng đá thạch anh đen vốn dĩ là loại vòng đá được rất nhiều người chọn lựa. Vòng đá thạch anh đen có sức mê hoặc người khác ngay từ cái nhìn đầu tiên. </span></h5><h5 style=\"text-align:justify;\"><span style=\"font-size:18pt;color:rgb(17,17,17);font-family:arial, helvetica, sans-serif;\">Vòng đeo tay đá thạch anh đen được chế tác từ loại đá thạch anh đen khai thác trong tự nhiên. Để có được sản phẩm vòng đá thạch anh đen như hiện tại thì người ta phải trải qua các kỹ thuật chế tác khác nhau như mài, cắt… Người ta còn gọi đá thạch anh đen là Morion. Loại đá này gần như là không trong suốt và chỉ có ánh sáng xuyên qua những lá mỏng mà thôi.</span></h5><h5 style=\"background:#FFFFFF;margin:0cm 0cm 15.6pt;text-align:justify;\"><span style=\"font-size:18pt;\"><span style=\"color:rgb(17,17,17);\">Vòng đá thạch anh đen là loại đá tự nhiên hấp thụ những nguồn năng lượng âm của đất trời. Việc đeo vòng đá thạch anh đen thường xuyên sẽ giúp cải thiện vận may, sức khỏe và hỗ trợ cho chủ nhân làm ăn phát đạt hơn</span><span style=\"color:rgb(17,17,17);\">.</span></span><br></h5><p style=\"background:#FFFFFF;margin:0cm 0cm 15.6pt 0cm;\"><span style=\"font-size:18pt;font-family:Arial;color:#333333;\"><img src=\"//media3.scdn.vn/img4/2020/08_19/xlf5wyNc770Vqtcei7v1_simg_d0daf0_800x1200_max.jpg\" style=\"margin:0 auto;display:block;\" alt=\"xlf5wyNc770Vqtcei7v1_simg_d0daf0_800x1200_max.jpg\"><br><br><br></span></p><p style=\"background:#FFFFFF;margin:0cm 0cm 15.6pt;text-align:center;\"><span style=\"font-size:18pt;\"><strong><span style=\"font-family:Arial;color:rgb(51,51,51);\"><span style=\"color:rgb(33,37,41);\">Cảm ơn quý khách hàng đã ghé thăm và ủng hộ shop!</span></span></strong></span></p><p style=\"background:#FFFFFF;margin:0cm 0cm 15.6pt;text-align:center;\"><span style=\"font-size:18pt;font-family:Arial;color:#333333;\"><span style=\"font-size:18pt;\"><strong><span style=\"font-size:22pt;\"><a href=\"https://www.sendo.vn/shop/dong-ho-trang-suc-vip/san-pham?p=5\">QUÝ KHÁCH XEM CÁC SẢN PHẨM KHÁC TẠI ĐÂY</a></span></strong></span></span></p><p style=\"background:#FFFFFF;margin:0cm 0cm 15.6pt 0cm;\"><span style=\"font-size:18pt;font-family:Arial;color:#333333;\"><br></span></p><p style=\"background:#FFFFFF;margin:0cm 0cm 15.6pt 0cm;\"><span style=\"font-size:18pt;font-family:Arial;color:#333333;\"><br></span></p><p style=\"background:#FFFFFF;margin:0cm 0cm 15.6pt 0cm;\"><span style=\"font-size:18pt;font-family:Arial;color:#333333;\"><br></span></p><h1><span style=\"font-size:8pt;\">Bánh Xe Pháp Luân, Bánh Xe Pháp Luân</span></h1><p><span style=\"font-size:8pt;\"><em><strong>Bánh Xe Pháp Luân, Bánh Xe Pháp Luân</strong></em></span></p><p style=\"background:#FFFFFF;margin:0cm 0cm 15.6pt;text-align:justify;\"><span style=\"font-size:18pt;font-family:arial, helvetica, sans-serif;\"><em><span style=\"color:rgb(51,51,51);\"><span style=\"color:#FF0000;\"><span style=\"color:#0000FF;\"></span></span></span></em></span></p><p></p></div>', '5', 'trangsuc', 'Bạc, Đá', 'Cả nam và nữ', 'Thái Lan', NULL);
INSERT INTO `trangsuc` VALUES (3, 'Vòng phong thủy, Vòng đá đốt trúc tự nhiên đa sắc 10li - VDmndottrucdasacS10', '99,000', 'uy không phải một mặt hàng thời trang mới lạ. Nhưng Vòng tay phong thủy đá tự nhiên vẫn giữ được độ “hot”.', '5', '2', NULL, '127.jpg,128.jpg,129.jpg,130.jpg,131.jpg,132.jpg,133.jpg', '<div><p style=\"text-align:center;\"><strong><span style=\"font-size:22pt;color:#262626;\">VÒNG TAY PHONG THỦY</span></strong></p><p><span style=\"font-size:12.5pt;color:#262626;\">&nbsp;</span><em><span style=\"font-size:12.5pt;color:#000000;\">Tuy không phải một mặt hàng thời trang mới lạ. Nhưng</span></em><strong><span style=\"font-size:12.5pt;color:#0000FF;\">&nbsp;Vòng tay phong thủy&nbsp;</span></strong><span style=\"font-size:12.5pt;color:#000000;\">đá tự nhiên&nbsp;<em>vẫn giữ được độ “hot”. Bởi vì nó là loại trang sức làm từ đá phong thủy. Chúng mang lại nhiều may mắn, tài lộc. Giúp tránh cái xấu, cái rủi. Giúp dưỡng tâm, thậm chí là chữa bệnh.</em></span></p><p><strong><span style=\"font-size:16pt;color:#00b050;\"><br></span></strong></p><p><strong><span style=\"font-size:16pt;color:#00b050;\"><img src=\"//media3.scdn.vn/img3/2019/8_6/uhAVBn_simg_0e75e0_483x483_max.jpg\" style=\"margin:0 auto;display:block;\" width=\"483\" height=\"483\" alt=\"uhAVBn_simg_0e75e0_483x483_max.jpg\"><br><br><br></span></strong></p><p><strong><span style=\"font-size:16pt;color:#00b050;\">THÔNG TIN SẢN PHẨM</span></strong><span style=\"font-size:12.5pt;color:#262626;\">&nbsp;<img src=\"//media3.scdn.vn/img3/2019/8_6/uhAVBn_simg_d0daf0_800x1200_max.jpg\" style=\"margin:0 auto;display:block;\" alt=\"uhAVBn_simg_d0daf0_800x1200_max.jpg\"></span></p><p><strong><span style=\"font-size:12.5pt;color:#262626;\">Màu Sắc: Đa sắc</span></strong></p><p><strong><span style=\"font-size:12.5pt;color:#262626;\">Hợp Mệnh:</span></strong><span style=\"font-size:12.5pt;color:#262626;\"> <span style=\"font-size:12.5pt;\">Ngũ Hành</span></span></p><p><strong><span style=\"font-size:12.5pt;color