INSERT INTO `vatphamphongthuy` VALUES (2, 'Cặp Tượng Rồng Đá Ngọc Hoàng Long Tự Nhiên Vàng Đậm, Quả Cầu Phong Thủy - TR_02_14,2', '2,350,000', 'Rồng là con vật có mình dài, thân có nhiều vảy, trên đầu có sừng như sừng  hươu, chân có móng vuốt, có thể tự do bay lượn trên trời cũng như dưới nước', '25', '8', NULL, '144.jpg,145.jpg,146.jpg,147.jpg,148.jpg,149.jpg,150.jpg', '<div><p><span style=\"font-size:14pt;\"><em><strong>Cặp Tượng Rồng Đá Ngọc Hoàng Long Tự Nhiên Vàng Đậm, Quả Cầu Phong Thủy</strong></em></span></p><p><span style=\"font-size:14pt;\"><strong></strong><em>Thông tin sản phẩm</em></span></p><p><span style=\"font-size:14pt;\">Tên: Cặp rồng nhả ngọc phong thủy</span></p><p><span style=\"font-size:14pt;\">Chất liệu: Đá ngọc hoàng long tự nhiên(canxit vàng)</span></p><p><span style=\"font-size:14pt;\">Kích thước: Cao:14,2cm; Rộng:7,3cm; dầy: 4,6cm</span></p><p><span style=\"font-size:14pt;\">Trọng lượng:1,0kg/ cặp</span></p><p><span style=\"font-size:14pt;\">Chế tác: Thủ công</span></p><p><span style=\"font-size:14pt;\">Xuất xứ: Việt Nam</span></p><p><span style=\"font-size:14pt;\">Sử dụng:Đặt kệ phòng khách, tủ, bàn làm việc....</span></p><p><span style=\"font-size:14pt;\">Sử dụng hoặc làm quà tặng cấp trên, bạn bè, đồng nghiệp....</span></p><p><span style=\"font-size:14pt;\"><strong>Chú ý:&nbsp;</strong><em>Sản phẩm gửi kèm 1cặp tượng, 1 quả cầu phong thủy nhỏ; chứng thư giám định đá tự nhiên, thẻ bảo hành chất lượng của công ty</em></span></p><p><span style=\"font-size:14pt;\"><em><strong>Hotline tư vấn: 0974701083; 0388341111</strong></em></span></p><p><span style=\"font-size:14pt;\"><em><strong><img src=\"//media3.scdn.vn/img3/2019/8_25/6fEPKW_simg_d0daf0_800x1200_max.jpg\" style=\"margin:0 auto;display:block;\" alt=\"6fEPKW_simg_d0daf0_800x1200_max.jpg\"><br><br></strong></em></span><img src=\"//media3.scdn.vn/img3/2019/6_16/hiCYJ9_simg_d0daf0_800x1200_max.jpg\" style=\"margin:0 auto;display:block;\" alt=\"hiCYJ9_simg_d0daf0_800x1200_max.jpg\"><span style=\"color:rgb(0,0,0);font-size:14pt;\"><strong></strong></span></p><p><span style=\"color:rgb(0,0,0);font-size:14pt;\"><strong>Ý nghĩa của rồng phong thủy</strong></span></p><p style=\"color:#333333;font-size:medium;\"><span style=\"color:rgb(0,0,0);font-size:14pt;\">&nbsp; &nbsp; &nbsp; &nbsp;Trong tứ linh, long lân quy phụng thì rồng là con vật đứng đầu tiên, do đó nó tượng trưng cho sự quyền lực, quyền lãnh đạo tối cao. Trong truyền thuyết rồng là con của trời, có thể gây mưa, mang đến cho mùa màng, cây cối sự xanh tốt, khí trời mát mẻ. Rồng có khả năng điều hòa nguyên khí của đất trời, ban phát sự tốt lành cho trần gian. Loài vật này là biểu tượng của sức mạnh vô biên, giúp cho doanh nhân làm ăn phát đạt nên nó có ý nghĩa tối cao trong phong thủy.</span></p><p style=\"color:#333333;font-size:medium;\"><span style=\"color:rgb(0,0,0);font-size:14pt;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Khi bày trí hình tượng con rồng hướng ra cửa chính hoặc cửa sổ sẽ thu hút vượng khí cho gia đình, công việc thuận buồm xuôi gió, phất lên cao như cánh diều gặp gió. Bên cạnh đó, nếu đặt cặp vật phong thủy rồng với phượng gia chủ sẽ có nhiều may mắn trong con đường tình duyên, cải thiện quan hệ vợ chồng. Bởi rồng và phượng là cặp đôi mang đến hạnh phúc, sự may mắn trong hôn nhân và gia đình.</span></p><h2 style=\"color:#005aa6;font-size:1.6em;\"><span style=\"color:rgb(0,0,0);\"><strong><span style=\"font-size:14pt;\"><img src=\"//media3.scdn.vn/img3/2019/6_16/facOXY_simg_d0daf0_800x1200_max.jpg\" style=\"margin:0 auto;display:block;\" alt=\"facOXY_simg_d0daf0_800x1200_max.jpg\"></span></strong></span></h2><h2 style=\"color:#005aa6;font-size:1.6em;\"><span style=\"color:rgb(0,0,0);\"><strong><span style=\"font-size:14pt;\">Công dụng rồng trong phong thủy</span></strong></span></h2><p style=\"color:#333333;font-size:medium;\"><span style=\"font-size:14pt;\"><span style=\"color:rgb(0,0,0);\">&nbsp; &nbsp; &nbsp;Rồng là con vật có mình dài, thân có nhiều vảy, trên đầu có sừng như sừng&nbsp; hươu, chân có móng vuốt, có thể tự do bay lượn trên trời cũng như dưới nước. Trong phong thủy, ngoài khả năng thu hút tài lộc, rồng còn có khả năng diệt trừ cái xấu, hóa giải tà khí</span><span style=\"color:rgb(0,0,0);\">. Loài vật phong thủy này rất thích hợp với những người làm trong lĩnh vực chính trị, kinh doanh.</span></span></p><p style=\"color:#333333;font-size:medium;\"><span style=\"color:rgb(0,0,0);\"><span style=\"font-size:14pt;\"><img src=\"//media3.scdn.vn/img3/2019/6_16/P0klFx_simg_d0daf0_800x1200_max.jpg\" style=\"margin:0 auto;display:block;\" alt=\"P0klFx_simg_d0daf0_800x1200_max.jpg\"></span><br><span style=\"font-size:14pt;\"><strong></strong></span></span></p><p style=\"color:#333333;font-size:medium;\"><span style=\"color:rgb(0,0,0);\"><span style=\"font-size:14pt;\"><strong>Vị trí đặt rồng phong thủy củng cố quyền lực</strong></span></span></p><p><span style=\"color:rgb(0,0,0);font-size:14pt;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Rồng nên được đặt ở vị trí thông thoáng, vì rồng là con vật tự do, cần một khoảng không gian để bay lượn. Do đó những nơi có nguồn năng lượng tốt sẽ phát huy hết tác dụng của vật phẩm phong thủy này.</span></p><p><span style=\"color:rgb(0,0,0);font-size:14pt;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Đặt rồng ở vị trí có thể quan sát hết tổng thể ngôi nhà, mắt rồng luôn hướng về phía rộng rãi, thoáng đãng, có như thế mới tăng được vượng tài cho ngôi nhà.</span></p><p><span style=\"color:rgb(0,0,0);font-size:14pt;\">&nbsp; &nbsp; &nbsp; &nbsp; Cửa sổ hay góc nhà là những khu vực không thích hợp với rồng phong thủy. Ngoài ra, không nên đặt đầu rồng hướng về phía cửa sổ là tốt nhất. Nếu rồng có cầm viên ngọc thì tránh để hướng tay của rồng hướng về cửa chính hoặc cửa sổ sẽ không giữ được tài lộc.</span></p><p><span style=\"color:rgb(0,0,0);font-size:14pt;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Ngoại trừ phượng thì không nên đặt các con vật phong thủy khác gần kề con rồng.</span></p><p><span style=\"color:rgb(0,0,0);font-size:14pt;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Hình tượng con rồng không nên đặt sau lưng ghế ngồi hoặc không được đối diện với người ngồi sẽ làm cho quyền lực bị áp chế, gây phản tác dụng, gây bất lợi về tài lộc, công danh sự nghiệp của chủ nhân.</span></p><p style=\"color:#333333;font-size:medium;\"><span style=\"color:rgb(0,0,0);font-size:14pt;\">&nbsp; &nbsp; &nbsp; &nbsp; Trước khi chọn những vật phẩm phong thủy bày trí trong nhà, gia chủ nên tìm hiểu thật kỹ. Áp dụng những quy tắc phong thủy trên khi đặt rồng phong thủy kết hợp với những yếu tố phong thủy của những bộ phận khác chắc chắn ngôi nhà, nơi làm việc của bạn sẽ tràn ngập vượng khí.</span></p><p style=\"color:#333333;font-size:medium;\"><span style=\"color:rgb(0,0,0);\"><span style=\"font-size:14pt;\"><img src=\"//media3.scdn.vn/img3/2019/6_16/9TEITM_simg_d0daf0_800x1200_max.jpg\" style=\"margin:0 auto;display:block;\" alt=\"9TEITM_simg_d0daf0_800x1200_max.jpg\"><img src=\"//media3.scdn.vn/img3/2019/6_16/epTrRb_simg_d0daf0_800x1200_max.jpg\" style=\"margin:0 auto;display:block;\" alt=\"epTrRb_simg_d0daf0_800x1200_max.jpg\"></span><br></span><span style=\"font-size:14pt;\"><span style=\"color:rgb(0,0,0);\"><br></span><strong>*Lưu ý</strong>&nbsp;</span></p><p><span style=\"color:#000000;font-size:14pt;\">&nbsp; &nbsp; &nbsp; &nbsp; Chúng tôi khuyên quý khách nên lựa chọn các vật phẩm phong thủy, trang sức phong thủy<em><strong>&nbsp; được làm bằng đá tự nhiên</strong></em>, bởi đá tự nhiên có độ bóng, độ bền theo thời gian và đá tự nhiên tích tụ năng lượng từ trường của tra\'i đất và phát huy tối đa công dụng trong phong thủy.</span></p><p><span style=\"font-size:14pt;\">- Chúng tôi xin cam kết mẫu sản phẩm giới thiệu và hình ảnh là thật 100%, là đá tự nhiên được chế tác thủ công.quý khách mua hàng có thể kiểm tra hàng trước khi thanh toán và tự chối nhận hàng nếu không giống mẫu giới thiệu</span></p><p><span style=\"font-size:14pt;\"><span style=\"color:rgb(29,33,41);\"><strong>Cam kết</strong>: Các s